import pygamefrom src.utils.constants import SCREEN_WIDTH, WHITE, GREENfrom src.ui.buttons import Buttonclass MainMenu:    def __init__(self, screen):        self.screen = screen        self.font = pygame.font.Font(None, 74)        self.small_font = pygame.font.Font(None, 50)        self.start_button = Button(SCREEN_WIDTH // 2 - 100, 200, 200, 50, "Начать игру", self.small_font, WHITE, GREEN)        self.settings_button = Button(SCREEN_WIDTH // 2 - 100, 300, 200, 50, "Настройки", self.small_font, WHITE, GREEN)        self.quit_button = Button(SCREEN_WIDTH // 2 - 100, 400, 200, 50, "Выход", self.small_font, WHITE, GREEN)    def handle_event(self, event):        self.start_button.handle_event(event)        self.settings_button.handle_event(event)        self.quit_button.handle_event(event)        if self.start_button.clicked:            print("Начать игру")            self.start_button.clicked = False        if self.settings_button.clicked:            print("Настройки")            self.settings_button.clicked = False        if self.quit_button.clicked:            print("Выход")            pygame.quit()            exit()    def draw(self):        title = self.font.render("Space Roguelike", True, WHITE)        self.screen.blit(title, (SCREEN_WIDTH // 2 - title.get_width() // 2, 50))        self.start_button.draw(self.screen)        self.settings_button.draw(self.screen)        self.quit_button.draw(self.screen)